import { UserActionTypes } from './user.types'

import { setCurrentUser } from './user.actions'

const INITIAL_STATE = {
  currentUser: null,
}

export const userReducer = (state = INITIAL_STATE, setCurrentUser) => {
  switch (setCurrentUser.type) {
    case 'SET_CURRENT_USER':
      return {
        ...state,
        currentUser: setCurrentUser.payload,
      }

    default:
      return state
  }
}

export default userReducer
