import { createSelector } from 'reselect'

const COLLECTION_ID_MAP = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  women: 4,
  men: 5,
}

const selectShop = (state) => state.shop

export const selectShopCollection = createSelector(
  [selectShop],
  (shop) => shop.collections
)

// export const selectCollection = (url) =>
//   createSelector([selectShopCollection], (collections) => {
//     collections.find((collection) => collection.id === COLLECTION_ID_MAP[url])
//   })

export function selectCollection(url) {
  createSelector([selectShopCollection])
}
